svg.script:
    const roles = ["Data_Enthusiasts","Coders","Learners","Researchers","Problem_Solvers","Innovators"];
    const matrixEl = document.getElementById("matrix");
    const letters = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";

    function randomChar() {
      return letters[Math.floor(Math.random() * letters.length)];
    }

    function typeRole(role) {
      let display = Array(role.length).fill(" ");
      return new Promise((resolve) => {
        const interval = setInterval(() => {
          let done = true;
          for (let i = 0; i < role.length; i++) {
            if (display[i] !== role[i]) {
              done = false;
              if (Math.random() < 0.85) {
                display[i] = randomChar();
              } else {
                display[i] = role[i];
              }
            }
          }
          matrixEl.textContent = `SELECT ${display.join("")} AS 'Welcome';`;
          if (done) {
            clearInterval(interval);
            setTimeout(resolve, 2000);
          }
        }, 120);
      });
    }

    (async function loop() {
      while (true) {
        for (const role of roles) {
          await typeRole(role);
        }
      }
    })();
